import { useContext } from 'react';
import { AllContext } from './context';
import { axios } from 'axios';


const Mycourses = () => {
   const {user,subjects,token}=useContext(AllContext)
   async function DeleteCourses(courses){
    let result= await axios.post('http://localhost:4000/courses',{courses:courses.subject,user:user.user_id})
    console.log(result);
   }

    return ( 
        <div>
        <table class="table table-hover">
     <tr>
      <th>name</th>
      <th>rating</th>
      <th>level</th>
     </tr>
     {user.course.map((course) => (
          <tr>
            <td>{course.subject} </td>
            <td>{course.rating} </td>
            <td>{course.level} </td>
            <td><button onClick={()=>DeleteCourses(course.subject)}>delete course</button></td>
          </tr>
  
        ))}
  
  
        </table>
  
      </div>
     );
}
 
export default Mycourses;
